    private void SUBMITActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SUBMITActionPerformed
    fd.Account_number=Accno.getText();
    fd.Date_of_birth=txt_dateofbirth.getText();
    fd.Email=txt_email.getText();
    fd.Local_Government=txt_localgovernment.getText();
    fd.Name=txt_name.getText();
    fd.Occupation=txt_occupation.getText();
    try{
   String query=("insert into atm(ACCOUNTNUMBER,NAME,DATE_OF_BIRTH,EMAIL,LOCAL_GOVERNMENT,OCCUPATION) Values(?,?,?,?,?,?)");     
        fd.pt=con.prepareStatement(query);
        fd.pt.setString(1,fd.Account_number);
        fd.pt.setString(2,fd.Name);
        fd.pt.setString(3,fd.Date_of_birth);
        fd.pt.setString(4,fd.Email);
        fd.pt.setString(5,fd.Local_Government);
        fd.pt.setString(6,fd.Occupation);
        fd.pt.execute();
        JOptionPane.showConfirmDialog(null, "enter YES to save record");
        JOptionPane.showMessageDialog(null, "record saved");
        con.close();
         Accno.setText("");
        txt_dateofbirth.setText("");
        txt_email.setText("");
        txt_localgovernment.setText("");
        txt_name.setText("");
        txt_occupation.setText("");
      
    }
    catch(Exception e){
        JOptionPane.showMessageDialog(null, e.getMessage());
    }
    }//GEN-LAST:event_SUBMITActionPerformed

