    private void SEARCHActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SEARCHActionPerformed
     try{
            String sql=("SELECT * from atm where ACCOUNTNUMBER="+txt_search.getText()+" ");
        fd.pt=con.prepareStatement(sql);
        fd.RS=fd.pt.executeQuery();
        while(fd.RS.next()){
       // Accno.setText(fd.RS.getString("ACCOUNTNUMBER"));
        txt_dateofbirth.setText(fd.RS.getString("DATE_OF_BIRTH"));
        txt_email.setText(fd.RS.getString("EMAIL"));
        txt_localgovernment.setText(fd.RS.getString("LOCAL_GOVERNMENT"));
        txt_name.setText(fd.RS.getString("NAME"));
        txt_occupation.setText(fd.RS.getString("OCCUPATION"));
             
            
            
            
        }
            
          
      }
      catch(Exception e){
          JOptionPane.showMessageDialog(null, e.getMessage());
          
      }
    }//GEN-LAST:event_SEARCHActionPerformed

